import axios from 'axios'
import React from 'react'
import { useRef } from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
// import FileDownloadIcon from '@mui/icons-material/FileDownload';


function StreamPage() {

    const [userData, setUserData] = useState()
    const [selectedRadio,setSelectedRadio] = useState
    const uploadFile = useRef();
    const check1=useRef();
    const check2=useRef();

    useEffect(() => {
        axios.post("http://localhost:5000/getData", { name: sessionStorage.getItem('Name') }) //json
            .then(response => {
                setUserData(response.data)

            })
    }, [])
    // console.log(userData)
    console.log(userData)

    function handleImage(e) {
        e.preventDefault()
        const file = uploadFile.current.files[0];
        // console.log(file, "testing image")
        var profilePic = new FormData();
        profilePic.append("file", file);
        // profilePic.append("file", file);

        console.log(file)
        // console.log(profilePic)
        axios.post("http://localhost:5000/upload", profilePic)

        sessionStorage.setItem("image", file.name)
        // window.location.reload();
    }

    function handleImg(e) {
        e.preventDefault()
        const file = uploadFile.current.files[0];
        file["UserName"] = sessionStorage.getItem('Name');
        file["fileName"] = file.name;
        file["typeOfFile"] = 'Personal';

        const newData = {
            name: sessionStorage.getItem('Name'),
            fileName: file.name,
            typeOfFile: check1.current.value
        }
        // console.log(file, "testing image")
        var profilePic = new FormData();
        profilePic.append("file", file);

        // profilePic.append("file", file);

        console.log(file)
        axios.post("http://localhost:5000/uploadNewFile", newData)
        // console.log(profilePic)
        axios.post("http://localhost:5000/upload", profilePic)

        sessionStorage.setItem("image", file.name)
        // window.location.reload();
    }

    const getImage = (data) => {

        axios.get(`http://localhost:5000/downloadImage/${data.filename}`).then((res) => {
            console.log(res, "ahsfahas")

        }).catch((err) => {
            console.log(err);
        }
        )
    }

    return (
        <div>

            <div className='right'>
                {/* <div className='head'> <h1>Upload New Files Here</h1></div> */}
                <link href="https://fonts.googleapis.com/css?family=Material+Icons|Material+Icons+Outlined" rel="stylesheet" />
                <form class="form-container" enctype='multipart/form-data'>
                    <div class="upload-files-container">
                        <div class="drag-file-area">
                            <span class="material-icons-outlined upload-icon"> file_upload </span>
                            <h3 class="dynamic-message"> Drag & drop any file here </h3>
                            <label class="label"> or <span class="browse-files">
                                <input type="file" class="default-file-input" ref={uploadFile} />
                                <span class="browse-files-text">browse file</span> <span>from device</span> </span> </label>
                        </div>
                        <span class="cannot-upload-message"> <span class="material-icons-outlined">error</span> Please select a file first <span class="material-icons-outlined cancel-alert-button">cancel</span> </span>
                        <div class="file-block">
                            <div class="file-info"> <span class="material-icons-outlined file-icon">description</span> <span class="file-name"> </span> | <span class="file-size">  <input type="radio" name="select" id="option-1" checked  value="Personal"/>
                                <input type="radio" name="select" id="option-2" onClick={(e)} value="Work"/> </span> </div>
                            <span class="material-icons remove-file-icon">delete</span>
                            <div class="progress-bar"> </div>
                        </div>
                        <div class="wrapper">
                        <input type="radio" name="select" id="option-1" checked onClick={(e)=>setSelectedRadio(e.target.value)} value="Personal"/>
                                <input type="radio" name="select" id="option-2" onClick={(e)=>setSelectedRadio(e.target.value)} value="Work"/>
                                    <label for="option-1" class="option option-1">
                                        <div class="dot"></div>
                                        <span>Personal</span>
                                    </label>
                                    <label for="option-2" class="option option-2">
                                        <div class="dot"></div>
                                        <span>Work</span>
                                    </label>
                                </div>

                                <button type="button" class="upload-button" onClick={handleImg}> Upload </button>
                        </div>
                </form>
                {/* <form className='uploadForm'>
                <input className='uploadHere' type="file" name="image" ref={uploadFile} style={{ paddingTop: "45px" }} />
                <button onClick={handleImage}>Upload </button>
            </form> */}
            </div>
            <div className='left'>
                <div class="datatable-container">

                    <div class="header-tools">
                        <div class="tools">
                            <ul>
                                <li>
                                    <button>
                                        <i class="material-icons">add_circle</i>
                                    </button>
                                </li>
                                <li>
                                    <button>
                                        <i class="material-icons">edit</i>
                                    </button>
                                </li>
                                <li>
                                    <button>
                                        <i class="material-icons">delete</i>
                                    </button>
                                </li>
                                <li>
                                    <button>
                                        <i class="material-icons">download</i>
                                    </button>
                                </li>
                            </ul>
                        </div>

                        <div class="search">
                            <input type="search" class="search-input" placeholder="Search..." />
                        </div>
                    </div>

                    <table class="datatable">
                        <thead>
                            <tr>
                                <th>id</th>
                                <th>File Name</th>
                                <th>Type Of File</th>
                                <th>Uploaded By</th>
                                {/* <th>length</th>
                                <th>Upload Date</th> */}
                                <th>Download</th>

                            </tr>
                        </thead>

                        <tbody>
                            {
                                userData && userData.map((data, i) => {
                                    return (
                                        <tr>
                                            <td>{i + 1}</td>
                                            <td>{data.fileName}</td>
                                            <td>{data.typeOfFile}</td>
                                            <td>{data.name}</td>
                                            {/* <td>{data.length}</td>
                                            <td>{data.uploadDate}</td> */}
                                            <td> <button onClick={() => getImage(data)}><i class="material-icons">download</i></button> </td>
                                        </tr>)
                                })
                            }
                        </tbody>
                    </table>


                    <div class="footer-tools">

                    </div>
                </div>
            </div>
        </div>
    )
}

export default StreamPage
