import axios from 'axios'
import React from 'react'
import { useRef } from 'react'
import { useState } from 'react'
import { useEffect } from 'react'


function StreamPage() {

    const [userData, setUserData] = useState([])

    const uploadFile = useRef();

    useEffect(() => {
        axios.get("http://localhost:5000/getData") //json
            .then(response => {
                setUserData(response.data)
                console.log(userData)

            })

    }, [])

    function handleImage(e) {
        e.preventDefault()
        const file = uploadFile.current.files[0];
        // console.log(file, "testing image")
        var profilePic = new FormData();
        profilePic.append("file", file);
        console.log(file)
        // console.log(profilePic)
        axios.post("http://localhost:5000/upload", profilePic)

        sessionStorage.setItem("image", file.name)
        // window.location.reload();
    }

    return (
        <div>
            <h2>Welcome to The Page</h2>
            <form >
                <input type="file" name="image" ref={uploadFile} style={{ paddingTop: "45px" }} />
                <button onClick={handleImage}>Upload </button>
            </form>

        </div>
    )
}

export default StreamPage
